
def decode(hex_string):
    decoded_bytes = bytes.fromhex(hex_string)
    decoded_string = decoded_bytes.decode('utf-8')
    return decoded_string

def encode(original_string):
# Encode the string to bytes first, then convert the bytes to hex format
    return original_string.encode('utf-8').hex()

def convert_hex_int(hex_string):
    return int(hex_string, 36)
